mapper.select:
SELECT o.id, o.number AS order_number, o.status, o.user_id, o.address_book_id, o.order_time, o.checkout_time, o.pay_method, o.pay_status, o.amount, o.remark, o.phone, o.address, o.user_name, o.consignee, o.cancel_reason, o.rejection_reason, o.cancel_time, o.estimated_delivery_time, o.delivery_status, o.delivery_time, o.pack_amount, o.tableware_number, o.tableware_status, od.id AS od_id, od.order_id, od.dish_id, od.setmeal_id, od.dish_flavor, od.name AS od_name, od.image, od.number AS od_number, od.amount AS od_amount FROM orders o LEFT JOIN order_detail od ON o.id = od.order_id
and o.user_id = #{userId}
and o.status = #{status}
ORDER BY o.order_time DESC

select * from orders


        and number like concat('%',#{number},'%')


        and phone like concat('%',#{phone},'%')


        and user_id = #{userId}


        and status = #{status}


        and order_time >= #{beginTime}


        and order_time <= #{endTime}


order by order_time desc

select sum(amount) from orders


        and order_time >= #{begin}


        and order_time <= #{end}


        and status = #{status}
